import { useMutation } from '@apollo/react-hooks';
import Router from 'next/router';
import { useInput } from '../hooks/useInput';
import * as S from './styles/Form';
import Error from './Error';
import { CREATE_ITEM } from '../graphql/mutations';

const CreateItem = () => {
  const [title, setTitle] = useInput('My title 2');
  const [price, setPrice] = useInput(200);
  const [description, setDescription] = useInput('My description 2');

  const [createItem, { loading, error }] = useMutation(CREATE_ITEM);

  const handleSubmit = async event => {
    event.preventDefault();
    const result = await createItem({ variables: { title, price, description } });

    Router.push({
      pathname: 'id',
      query: {
        id: result.data.createItem,
      },
    });
  };

  return (
    <S.Form onSubmit={handleSubmit}>
      <Error error={error} />
      <fieldset diabled={loading} aria-busy={loading}>
        <label htmlFor="title">
          Title
          <input type="text" name="title" placeholder="Title" required value={title} onChange={setTitle} />
        </label>
        <label htmlFor="price">
          Price
          <input type="number" name="price" placeholder="Price" required value={price} onChange={setPrice} />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            name="description"
            placeholder="Description"
            required
            value={description}
            onChange={setDescription}
          />
        </label>
        <button type="submit">Submit</button>
      </fieldset>
    </S.Form>
  );
};

export default CreateItem;
