import { useMutation } from '@apollo/react-hooks';
import { useInput } from '../hooks/useInput';
import * as S from './styles/Form';
import { ADD_ITEM } from '../graphql/mutations';

const createItem = () => {
  const [title, setTitle] = useInput('');
  const [price, setPrice] = useInput(0);
  const [description, setDescription] = useInput('');
  const [addTodo, { data }] = useMutation(ADD_ITEM);

  const handleSubmit = async event => {
    event.preventDefault();
    await addTodo({ variables: { title, price, description } });

    console.log(data);
  };

  return (
    <S.Form onSubmit={handleSubmit}>
      <fieldset>
        <label htmlFor="title">
          Title
          <input type="text" name="title" placeholder="Title" required value={title} onChange={setTitle} />
        </label>
        <label htmlFor="price">
          Price
          <input type="number" name="price" placeholder="Price" required value={price} onChange={setPrice} />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            name="description"
            placeholder="Description"
            required
            value={description}
            onChange={setDescription}
          />
        </label>
        <button type="submit">Submit</button>
      </fieldset>
    </S.Form>
  );
};

export default createItem;
